import numpy as np
class SimpleNN:
    def __init__(self):
        self.weights = np.random.randn(1)
    def forward(self, x):
        return np.dot(x, self.weights)
    def train(self, x, y, lr=0.01):
        pred = self.forward(x)
        self.weights += lr * (y - pred) * x

nn = SimpleNN()
nn.train(np.array([1]), np.array([2]))

